import { useState } from "react";
import PropTypes from "prop-types";
import SearchIcon from "./SearchIcon";
import miniLogo from "../../img/pokeball.png";

function SearchBar({ placeholder, onSearch, onTypeFilterChange }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState(null);

  const handleSearch = () => {
    onSearch(searchTerm);
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleTypeFilterChange = (event) => {
    const selectedType = event.target.value;
    setTypeFilter(selectedType);
    onTypeFilterChange(selectedType);
  };

  return (
    <div>
      {/* Search input */}
      <input
        type="text"
        placeholder={placeholder}
        value={searchTerm}
        onChange={handleInputChange}
      />

      {/* Filter options */}
      <select value={typeFilter} onChange={handleTypeFilterChange}>
        <option value="">All Types</option>
        <option value="fire">Fire</option>
        <option value="water">Water</option>
        {/* Add more options for other types */}
      </select>

      {/* Search button */}
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

SearchBar.propTypes = {
  placeholder: PropTypes.string,
  onSearch: PropTypes.func.isRequired,
  onTypeFilterChange: PropTypes.func.isRequired,
};

export default SearchBar;
