import { useState, useMemo } from "react";
import PropTypes from "prop-types";
import SearchIcon from "./SearchIcon";
import miniLogo from "../../img/pokeball.png";

const pokemonTypes = [
  "normal", "fighting", "flying", "poison", "ground", 
  "rock", "bug", "ghost", "steel", "fire", 
  "water", "grass", "electric", "psychic", "ice", 
  "dragon", "dark", "fairy"
];

function SearchBar({ placeholder, onSearch, onTypeFilterChange, onSortOptionChange }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState(null);
  const [sortOption, setSortOption] = useState(null);

  const handleSearch = () => {
    onSearch(searchTerm);
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleTypeFilterChange = (event) => {
    const selectedType = event.target.value;
    setTypeFilter(selectedType);
    onTypeFilterChange(selectedType);
  };

  const handleSortOptionChange = (event) => {
    const selectedOption = event.target.value;
    setSortOption(selectedOption);
    onSortOptionChange(selectedOption);
  };

  const renderTypeOptions = () => {
    return pokemonTypes.map((type) => (
      <option key={type} value={type}>{type}</option>
    ));
  };

  return (
    <div className="flex items-center justify-between w-96 p-2 rounded-3xl shadow-md bg-white focus-within:ring-2 focus-within:ring-yellowTheme focus-within:outline-none">
      <div className="flex items-center justify-between w-full">
        <img
          src={miniLogo}
          alt="PokeDex Logo"
          className="w-9 h-9 hidden md:block"
          draggable="false"
        />
        <img
          src={miniLogo}
          alt="PokeDex Logo"
          className="w-9 h-9 block md:hidden cursor-pointer"
          draggable="false"
        />
        <input
          type="text"
          placeholder={placeholder}
          className="mx-2 w-full outline-none bg-white text-blueTheme"
          value={searchTerm}
          onChange={handleInputChange}
        />
        <SearchIcon onClick={handleSearch} />
        <select value={typeFilter} onChange={handleTypeFilterChange}>
          <option value="">Filter by type...</option>
          {renderTypeOptions()}
        </select>
        <select value={sortOption} onChange={handleSortOptionChange}>
          <option value="">Sort by...</option>
          <option value="heightDesc">Height: High to Low</option>
          <option value="heightAsc">Height: Low to High</option>
          <option value="weightDesc">Weight: Heavy to Light</option>
          <option value="weightAsc">Weight: Light to Heavy</option>
        </select>
      </div>
    </div>
  );
}

SearchBar.propTypes = {
  placeholder: PropTypes.string,
  onSearch: PropTypes.func.isRequired,
  onTypeFilterChange: PropTypes.func.isRequired,
  onSortOptionChange: PropTypes.func.isRequired,
};

export default SearchBar;
