import mainLogo from "../../img/pokedexlogo.png";
import SearchBar from "../ui/SearchBar";
import PropTypes from "prop-types";

function MainNavigation({ onSearch, sortOption, setSortOption }) {
  const handleClick = () => {
    onSearch("");
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  return (
    <header className="w-full h-27 flex items-center justify-center bg-blueTheme px-10 rounded-bl-3xl rounded-br-3xl">
      <div className="container mx-0">
        <div className="hidden items-center justify-between mx-0 md:flex md:w-full">
          <div className="flex justify-start w-1/2">
            <img
              src={mainLogo}
              alt="Pokedex Logo"
              draggable="false"
              className="max-w-full h-26 cursor-pointer"
              onClick={handleClick}
            />
          </div>
          <div className="flex justify-end w-1/2">
            <SearchBar
              placeHolder="Search Pokemon by name or PokeID"
              onSearch={onSearch}
            />
            <select value={sortOption} onChange={handleSortChange}>
              <option value="">Sort by...</option>
              <option value="heightDesc">Height: High to Low</option>
              <option value="heightAsc">Height: Low to High</option>
              <option value="weightDesc">Weight: Heavy to Light</option>
              <option value="weightAsc">Weight: Light to Heavy</option>
            </select>
          </div>
        </div>
        <div className="flex items-center justify-center mx-auto w-full md:hidden">
          <div className="flex justify-center w-full">
            <SearchBar placeHolder="Search Pokemon" onSearch={onSearch} />
          </div>
        </div>
      </div>
    </header>
  );
}

MainNavigation.propTypes = {
  onSearch: PropTypes.func,
  sortOption: PropTypes.string,
  setSortOption: PropTypes.func,
};

export default MainNavigation;
