import { useState, useMemo, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import Axios from "axios";
import PropTypes from "prop-types";
import "tailwindcss/tailwind.css"

import BackgroundImage from "../components/ui/BackgroundImage";
import CardsContainer from "../components/ui/CardComponents/CardsContainer";
import SmallCard from "../components/ui/CardComponents/SmallCard";
import Loader from "../components/ui/Loader.js";
import Error from "./errors/Error";
import Pagination from "../components/ui/Pagination";

function Home({ searchTerm, sortOption, openBigCard }) {
  const [page, setPage] = useState(1);
  const [sortedPokemonData, setSortedPokemonData] = useState([]);
  const limit = 100;

  const [randomPokemon, setRandomPokemon] = useState(null);

  const [heightFilter, setHeightFilter] = useState(null);
  const [weightFilter, setWeightFilter] = useState(null);

  const fetchPokemon = async () => {

    let url = null;
    if (heightFilter === "taller100cm") {
      url = "https://pokeapi.co/api/v2/pokemon?height.gte=100&offset=0&limit=10";
    } else if (heightFilter === "shorter100cm") {
      url = "https://pokeapi.co/api/v2/pokemon?height.lt=100&offset=0&limit=10";
    } else if (weightFilter === "0-10kg") {
      url = "https://pokeapi.co/api/v2/pokemon?weight.gt=0&weight.lte=10&offset=0&limit=10";
    } else if (weightFilter === "11-50kg") {
      url = "https://pokeapi.co/api/v2/pokemon?weight.gt=10&weight.lte=50&offset=0&limit=10";
    } else if (weightFilter === "more50kg") {
      url = "https://pokeapi.co/api/v2/ability?offset=0&limit=10";
    }

    if (searchTerm) {
      const response = await Axios.get(
        `https://pokeapi.co/api/v2/pokemon/${searchTerm.toLowerCase()}`
      );
      return [response.data];
    } else {
      const response = await Axios.get(
        url || `https://pokeapi.co/api/v2/pokemon?offset=${(page - 1) * limit}&limit=${limit}`
      );
      const { results } = response.data;
      const responses = await Promise.all(results.map((result) => Axios.get(result.url)));
      return responses.map((res) => res.data);
    }
  };

  const {
    data: pokemonData,
    isLoading,
    isError,
  } = useQuery(["pokemon", searchTerm, heightFilter, weightFilter, page], fetchPokemon);

  useEffect(() => {
    if (pokemonData) {
      let sortedData = [...pokemonData];

      switch (sortOption) {
        case "heightDesc":
          sortedData.sort((a, b) => b.height - a.height);
          break;
        case "heightAsc":
          sortedData.sort((a, b) => a.height - b.height);
          break;
        case "weightDesc":
          sortedData.sort((a, b) => b.weight - a.weight);
          break;
        case "weightAsc":
          sortedData.sort((a, b) => a.weight - b.weight);
          break;
        default:
          break;
      }

      setSortedPokemonData(sortedData);
    }
  }, [pokemonData, sortOption]);

  const getSprite = useMemo(
    () => (data) => {
      if (data?.id >= 906 && data?.id <= 1008) {
        return data.sprites.other["official-artwork"]["front_default"];
      } else if (data?.id < 1009) {
        return data.sprites.other.home.front_default;
      }
      return null;
    },
    []
  );

  const renderPokemonCard = (pokemon) => {
    const image = getSprite(pokemon);
    return (
      <SmallCard
        height={`${pokemon.height * 10}`}
        id={pokemon.id}
        image={image}
        key={pokemon.id}
        name={pokemon.species.name}
        weight={`${pokemon.weight / 10}`}
        openBigCard={openBigCard}
      />
    );
  };

  const handleHeightFilterClick = (filter) => {
    if (heightFilter === filter) {
      setHeightFilter(null);
    } else {
      setHeightFilter(filter);
    }
    setPage(1);
  };

  const handleWeightFilterClick = (filter) => {
    if (weightFilter === filter) {
      setWeightFilter(null);
    } else {
      setWeightFilter(filter);
    }
    setPage(1);
  };

  const handleRandomPokemonClick = async () => {
    try {
      const response = await Axios.get("https://pokeapi.co/api/v2/pokemon?limit=1118");
      const totalPokemon = response.data.count;
      const randomPokemonId = Math.floor(Math.random() * totalPokemon) + 1;
      const randomResponse = await Axios.get(`https://pokeapi.co/api/v2/pokemon/${randomPokemonId}`);
      setRandomPokemon(randomResponse.data);
    } catch (error) {
      console.error("Error fetching random Pokémon:", error);
    }
  };

  return (
    <BackgroundImage>
      <div className="flex">
        <div className="bg-gray-200 p-4 w-64">
          <h3 className="mt-0">Filters</h3>
          <a
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={() => handleHeightFilterClick("taller100cm")}
          >
            Pokémon Taller than 100cm
          </a>
          <a
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={() => handleHeightFilterClick("shorter100cm")}
          >
            Pokémon Shorter than 100cm
          </a>
          <a
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={() => handleWeightFilterClick("0-10kg")}
          >
            Pokémon Weighing 0-10kg
          </a>
          <a
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={() => handleWeightFilterClick("11-50kg")}
          >
            Pokémon Weighing 11-50kg
          </a>
          <a
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={() => handleWeightFilterClick("more50kg")}
          >
            Pokémon Weighing more than 50kg
          </a>
          <button
            className="block mb-2 text-blue-500 cursor-pointer hover:font-semibold"
            onClick={handleRandomPokemonClick}
          >
            Generate a Random Pokémon
          </button>
          <button
          className="block mb-2 text-red-500 cursor-pointer hover:font-semibold"
          onClick={() => window.location.reload()}
        >
          Reset
        </button>
        </div>
        <div className="ml-4 flex-grow">
          {isLoading ? (
            <Loader />
          ) : isError || (!sortedPokemonData[0]?.id && !randomPokemon) ? (
            <Error />
          ) : (
            <>
              <Pagination
                page={page}
                handlePrevClick={() => setPage((prev) => Math.max(prev - 1, 1))}
                handleNextClick={() => setPage((prev) => prev + 1)}
                handleFirstClick={() => setPage(1)}
                handleLastClick={() => setPage(11)}
              />
              <CardsContainer>
                {randomPokemon ? (
                  renderPokemonCard(randomPokemon)
                ) : (
                  sortedPokemonData.map(renderPokemonCard)
                )}
              </CardsContainer>
              <Pagination
                page={page}
                handlePrevClick={() => setPage((prev) => Math.max(prev - 1, 1))}
                handleNextClick={() => setPage((prev) => prev + 1)}
                handleFirstClick={() => setPage(1)}
                handleLastClick={() => setPage(11)}
              />
            </>
          )}
        </div>
      </div>
    </BackgroundImage>
  );
}

Home.propTypes = {
  searchTerm: PropTypes.string,
  sortOption: PropTypes.string,
  openBigCard: PropTypes.func.isRequired,
};

export default Home;
