import { useState } from "react";
import PropTypes from "prop-types";
import SearchIcon from "./SearchIcon";
import miniLogo from "../../img/pokeball.png";

function SearchBar({ placeholder, onSearch, onTypeFilterChange }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState(null);

  const handleSearch = () => {
    onSearch(searchTerm);
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleTypeFilterChange = (event) => {
    const selectedType = event.target.value;
    setTypeFilter(selectedType);
    onTypeFilterChange(selectedType);
  };

  return (
    <div className=" items-center justify-between w-96 p-2 rounded-3xl shadow-md bg-white focus-within:ring-2 focus-within:ring-yellowTheme focus-within:outline-none">
      <div className=" flex items-center justify-between w-full">
        <img
          src={miniLogo}
          alt="PokeDex Logo"
          className="w-9 h-9 hidden md:block "
          onClick={handleIconClick}
          draggable="false"
        />
        <img
          src={miniLogo}
          alt="PokeDex Logo"
          className="w-9 h-9 block md:hidden cursor-pointer"
          onClick={handleSmallScreenClick}
          draggable="false"
        />
        <input
          type="text"
          placeholder={placeHolder}
          className="mx-2 w-full outline-none bg-white text-blueTheme"
          ref={inputRef}
          value={searchTerm}
          onChange={handleInputChange}
          onKeyDown={handleKeyPress}
        />
        <SearchIcon onClick={handleSearch} />
      </div>
    </div>
  );
}

SearchBar.propTypes = {
  placeholder: PropTypes.string,
  onSearch: PropTypes.func.isRequired,
};

export default SearchBar;
